import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import newRequest from '../../utils/newRequest';
import './success.scss';

const Success = () => {
  const { search } = useLocation();
  const navigate = useNavigate();
  const params = new URLSearchParams(search);
  const payment_intent = params.get('payment_intent');

  useEffect(() => {
    const makeRequest = async () => {
      try {
        if (!payment_intent) {
          throw new Error('Payment intent not found');
        }

        // Sending Ethereum transaction ID to backend
        const response = await newRequest.put('/orders', { payment_intent });
        const data = await response.json();

        console.log('Order update response:', data);

        setTimeout(() => {
          navigate("/orders");
        }, 5000);
      } catch (error) {
        console.error('Error updating order:', error);
      }
    }

    makeRequest();
  }, [navigate, payment_intent]);

  return (
    <>
      <div className="cm">
        <img src="images/successfully-done.gif" alt="Transaction Completed Successfully" />
      </div>
      <div className='success'>
        Payment successful! You are being redirected to the order page.<br></br>
      </div>
      <span className='close'>Please do not close this page.</span>
    </>
  );
}

export default Success;
